          image:
            "https://media.discordapp.net/attachments/867638574571323424/1205876205367201934/Vault_FloorThree1.png?ex=65d9f6bb&is=65c781bb&hm=a83c1bc673772a68e4255c62ef7d9ea974cf9dc8051e55484d01c1fdef9cbc62&",
